import React, { useState } from 'react';
import NavBarView from '../components/NavBarView';
import {
  Box,
  Button,
  Card,
  CardMedia,
  Grid,
  IconButton,
  Typography,
} from '@mui/material';
import { purple, red } from "@mui/material/colors";
import DeleteIcon from "@mui/icons-material/Delete";
import { Close } from "@mui/icons-material";
import EditIcon from "@mui/icons-material/Edit";
import Footer from '../components/Footer';
import { useNavigate } from 'react-router-dom';
import { Table, TableHead, TableBody, TableRow, TableCell, TableContainer, Paper, Dialog, DialogTitle, DialogContent, TextField } from '@mui/material';

function Dashboard() {
  const navigate = useNavigate();

  const [products, setProducts] = useState([
    {
      id: 1,
      productName: 'Product 1',
      supplier: 'Supplier A',
      dateReceived: '2023-10-18',
      expiryDate: '2023-12-31',
      originalPrice: 10,
      sellingPrice: 15,
      qty: 100,
      totalPrice: 1500,
    },
    {
      id: 2,
      productName: 'Product 2',
      supplier: 'Supplier B',
      dateReceived: '2023-10-19',
      expiryDate: '2024-01-31',
      originalPrice: 12,
      sellingPrice: 18,
      qty: 80,
      totalPrice: 1440,
    },
    {
        id: 3,
        productName: 'Product 3',
        supplier: 'Supplier C',
        dateReceived: '2023-11-19',
        expiryDate: '2024-01-31',
        originalPrice: 10,
        sellingPrice: 20,
        qty: 80,
        totalPrice: 800,
      },
      {
        id: 4,
        productName: 'Product 4',
        supplier: 'Supplier D',
        dateReceived: '2023-09-19',
        expiryDate: '2023-11-31',
        originalPrice: 15,
        sellingPrice: 22,
        qty: 60,
        totalPrice: 1000,
      },
      {
        id: 5,
        productName: 'Product 4',
        supplier: 'Supplier E',
        dateReceived: '2023-10-19',
        expiryDate: '2024-01-31',
        originalPrice: 20,
        sellingPrice: 23,
        qty: 70,
        totalPrice: 1200,
      },
    // Add more product entries as needed
  ]);

  const [openEditProductDialog, setOpenEditProductDialog] = useState(false);
  const [editedProduct, setEditedProduct] = useState({
    id: null,
    productName: '',
    supplier: '',
    dateReceived: '',
    expiryDate: '',
    originalPrice: 0,
    sellingPrice: 0,
    qty: 0,
    totalPrice: 0,
  });

  const [openAddProductDialog, setOpenAddProductDialog] = useState(false);
  const [newProduct, setNewProduct] = useState({
    id: null,
    productName: '',
    supplier: '',
    dateReceived: '',
    expiryDate: '',
    originalPrice: 0,
    sellingPrice: 0,
    qty: 0,
    totalPrice: 0,
  });

  const handleOpenEditProductDialog = (product) => {
    setEditedProduct(product);
    setOpenEditProductDialog(true);
  };

  const handleCloseEditProductDialog = () => {
    setOpenEditProductDialog(false);
  };

  const handleSaveEditedProduct = () => {
    const updatedProducts = products.map((product) => {
      if (product.id === editedProduct.id) {
        return editedProduct;
      }
      return product;
    });
    setProducts(updatedProducts);
    setOpenEditProductDialog(false);
  };

  const handleOpenAddProductDialog = () => {
    setOpenAddProductDialog(true);
  };

  const handleCloseAddProductDialog = () => {
    setOpenAddProductDialog(false);
  };

  const handleAddProduct = () => {
    const newProductId = Date.now();
    setNewProduct({ ...newProduct, id: newProductId });
    setProducts([...products, newProduct]);
    setOpenAddProductDialog(false);
  };

  const handleDeleteProduct = (productId) => {
    const updatedProducts = products.filter((product) => product.id !== productId);
    setProducts(updatedProducts);
  };

  return (
    <>
      <NavBarView />
      <Box sx={{ width: '100%',height:"600px",backgroundColor:"papayawhip" }}>
        <Box sx={{ width: '100%' }}>
          <Typography sx={{ fontSize: { md: '40px', xs: '30px' }, paddingLeft: '15vw', fontWeight: 'bold' }}>Dashboard</Typography>
        </Box>

        <Box sx={{
          px: { xs: "10px", md: "100px", lg: "200px" },
          py: 5,
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
        }}>
          <TableContainer component={Paper}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Product Name</TableCell>
                  <TableCell>Supplier</TableCell>
                  <TableCell>Date Received</TableCell>
                  <TableCell>Expiry Date</TableCell>
                  <TableCell>Original Price</TableCell>
                  <TableCell>Selling Price</TableCell>
                  <TableCell>QTY</TableCell>
                  <TableCell>Total Price</TableCell>
                  <TableCell>Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {products.map((product) => (
                  <TableRow key={product.id}>
                    <TableCell>{product.productName}</TableCell>
                    <TableCell>{product.supplier}</TableCell>
                    <TableCell>{product.dateReceived}</TableCell>
                    <TableCell>{product.expiryDate}</TableCell>
                    <TableCell>${product.originalPrice}</TableCell>
                    <TableCell>${product.sellingPrice}</TableCell>
                    <TableCell>{product.qty}</TableCell>
                    <TableCell>${product.totalPrice}</TableCell>
                    <TableCell>
                      <IconButton
                        variant="contained"
                        color="primary"
                        style={{ backgroundColor: purple[500], color: "#fff", marginRight: "8px" }}
                        onClick={() => handleOpenEditProductDialog(product)}
                      
                      >
                         <EditIcon style={{ color: "#fff" }} />
                      </IconButton>
                      <IconButton
                        variant="contained"
                        color="secondary"
                        style={{ backgroundColor: red[500], color: "#fff" }}
                        onClick={() => handleDeleteProduct(product.id)}
                      >
                       <DeleteIcon style={{ color: "#fff" }} />
                      </IconButton>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
          </Box>
          <Box sx={{ width: '100%', display: 'flex', alignItems: 'center', paddingX: '13.6vw',justifyContent: 'left' }}>
        <Button
          variant="contained"
          color="primary"
          width="180px"
          onClick={handleOpenAddProductDialog}
        >
          Add Product
        </Button>
      
        </Box>
      </Box>
     

      {/* Edit Product Dialog */}
      <Dialog open={openEditProductDialog} onClose={handleCloseEditProductDialog}>
        <DialogTitle>Edit Product</DialogTitle>
        <DialogContent>
          <TextField
            label="Product Name"
            value={editedProduct.productName}
            onChange={(e) => setEditedProduct({ ...editedProduct, productName: e.target.value })}
            sx={{ marginBottom: '10px', width: '80%' }}
          />
          <TextField
            label="Supplier"
            value={editedProduct.supplier}
            onChange={(e) => setEditedProduct({ ...editedProduct, supplier: e.target.value })}
            sx={{ marginBottom: '10px', width: '80%' }}
          />
          <TextField
            label="Date Received"
            value={editedProduct.dateReceived}
            onChange={(e) => setEditedProduct({ ...editedProduct, dateReceived: e.target.value })}
            sx={{ marginBottom: '10px', width: '80%' }}
          />
          <TextField
            label="Expiry Date"
            value={editedProduct.expiryDate}
            onChange={(e) => setEditedProduct({ ...editedProduct, expiryDate: e.target.value })}
            sx={{ marginBottom: '10px', width: '80%' }}
          />
          <TextField
            label="Original Price"
            value={editedProduct.originalPrice}
            onChange={(e) => setEditedProduct({ ...editedProduct, originalPrice: parseFloat(e.target.value) })}
            sx={{ marginBottom: '10px', width: '80%' }}
          />
          <TextField
            label="Selling Price"
            value={editedProduct.sellingPrice}
            onChange={(e) => setEditedProduct({ ...editedProduct, sellingPrice: parseFloat(e.target.value) })}
            sx={{ marginBottom: '10px', width: '80%' }}
          />
          <TextField
            label="QTY"
            value={editedProduct.qty}
            onChange={(e) => setEditedProduct({ ...editedProduct, qty: parseInt(e.target.value) })}
            sx={{ marginBottom: '10px', width: '80%' }}
          />
          <Button variant="contained" color="primary" onClick={handleSaveEditedProduct}>Save</Button>
          <Button variant="contained" color="secondary" onClick={handleCloseEditProductDialog}>Cancel</Button>
        </DialogContent>
      </Dialog>

      {/* Add Product Dialog */}
      <Dialog open={openAddProductDialog} onClose={handleCloseAddProductDialog}>
        <DialogTitle>Add Product</DialogTitle>
        <DialogContent>
          <TextField
            label="Product Name"
            value={newProduct.productName}
            onChange={(e) => setNewProduct({ ...newProduct, productName: e.target.value })}
          />
          <TextField
            label="Supplier"
            value={newProduct.supplier}
            onChange={(e) => setNewProduct({ ...newProduct, supplier: e.target.value })}
          />
          <TextField
            label="Date Received"
            value={newProduct.dateReceived}
            onChange={(e) => setNewProduct({ ...newProduct, dateReceived: e.target.value })}
          />
          <TextField
            label="Expiry Date"
            value={newProduct.expiryDate}
            onChange={(e) => setNewProduct({ ...newProduct, expiryDate: e.target.value })}
          />
          <TextField
            label="Original Price"
            value={newProduct.originalPrice}
            onChange={(e) => setNewProduct({ ...newProduct, originalPrice: parseFloat(e.target.value) })}
          />
          <TextField
            label="Selling Price"
            value={newProduct.sellingPrice}
            onChange={(e) => setNewProduct({ ...newProduct, sellingPrice: parseFloat(e.target.value) })}
          />
          <TextField
            label="QTY"
            value={newProduct.qty}
            onChange={(e) => setNewProduct({ ...newProduct, qty: parseInt(e.target.value) })}
          />
          <Button variant="contained" color="primary" onClick={handleAddProduct}>Add</Button>
          <Button variant="contained" color="secondary" onClick={handleCloseAddProductDialog}>Cancel</Button>
        </DialogContent>
      </Dialog>

    
      <Footer />
    </>
  );
}

export default Dashboard;
