import React, { useEffect, useState } from "react";
import NavBarView from "../components/NavBarView";
import {
  Box,
  Button,
  Card,
  CardMedia,
  Grid,
  IconButton,
  Typography,
} from "@mui/material";
import AddIcon from "@mui/icons-material/Add";
import { Close, Add, Remove } from "@mui/icons-material";
import Footer from "../components/Footer";
import ReplyIcon from "@mui/icons-material/Reply";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Cart() {
  const navigate = new useNavigate();
  useEffect((e) => {
    getCart(e);
  }, []);

  const [cartDetails, setCartDetails] = useState([]);

  const ShoppingBack = () => {
    navigate("/shop");
  };
  const navigateLogin = () => {
    navigate("/Checkout");
  };

  const getCart = async (e) => {
    const url = "http://localhost:5000/getcart";
    try {
      const response = await axios.get(url);
      setCartDetails(response.data);
    } catch (error) {
      alert(error);
    }
  };

  const deleteItem = async (e, data) => {
    const url = "http://localhost:5000/deleteItem";
    e.preventDefault();
    try {
      const response = await axios.post(url, { name: data });
      setCartDetails(response.data);
    } catch (error) {
      alert(error);
    }
  };

  const handleQuantityChange = async (e, cart, newQuantity) => {
    e.preventDefault();
    const url = "http://localhost:5000/updateQuantity";
    try {
      const response = await axios.post(url, {
        name: cart.name,
        quantity: newQuantity,
      });
      setCartDetails(response.data);
    } catch (error) {
      alert(error);
    }
  };

  return (
    <>
      <NavBarView />
      <Box sx={{ width: "100%" }}>
        <Box sx={{ width: "100%" }}>
          <Typography
            sx={{
              fontSize: { md: "40px", xs: "30px" },
              paddingLeft: "15vw",
              fontWeight: "bold",
            }}
          >
            My Cart
          </Typography>
        </Box>

        {/* Cart items */}
        <Box
          sx={{
            px: { xs: "25px", md: "100px", lg: "200px" },
            py: 5,
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
          }}
        >
          {cartDetails.map((cart) => (
            <Card
              key={cart.name}
              sx={{
                position: "relative",
                p: 1,
                height: { xs: "100px", md: "130px" },
                border: "1px solid #d9d9d9",
                marginBottom: "10px",
              }}
            >
              <Grid
                container
                direction="row"
                spacing={2}
                sx={{ alignItems: "center" }}
              >
                <Grid item xs={3} sm={2} md={1.5}>
                  <CardMedia
                    component="img"
                    image={cart.image}
                    alt={cart.name}
                    sx={{
                      height: { xs: "80px", sm: "82px", md: "113px" },
                    }}
                  />
                </Grid>

                <Grid item xs={2.5} md={3}>
                  <Typography
                    variant="h6"
                    sx={{
                      fontSize: { xs: "13px", md: "18px" },
                      fontWeight: 400,
                    }}
                  >
                    {cart.name}
                  </Typography>
                </Grid>

                <Grid item xs={3.5} md={3.5}>
                  <Box sx={{ display: "flex", alignItems: "center" }}>
                    <Typography
                      sx={{
                        display: { xs: "none", sm: "none", md: "block" },
                        fontSize: "16px",
                      }}
                    >
                      Quantity
                    </Typography>
                    <IconButton
                      onClick={(e) =>
                        handleQuantityChange(e, cart, cart.quantity - 1)
                      }
                      disabled={cart.quantity === 1}
                    >
                      <Remove />
                    </IconButton>
                    <Typography
                      variant="body2"
                      sx={{ marginLeft: 1, marginRight: 1 }}
                    >
                      {cart.quantity}
                    </Typography>
                    <IconButton
                      onClick={(e) =>
                        handleQuantityChange(e, cart, cart.quantity + 1)
                      }
                    >
                      <AddIcon
                            sx={{
                              background: "orange ",
                              borderRadius: "50%",
                              color: "black",
                            }}
                          />

                    </IconButton>
                  </Box>
                </Grid>

                <Grid item xs={2.5} md={3.5}>
                  <Box sx={{ display: "flex", alignItems: "center" }}>
                    <Typography
                      sx={{
                        display: { xs: "none", md: "block" },
                        fontSize: "16px",
                      }}
                    >
                      {cart.price}
                    </Typography>
                  </Box>
                </Grid>
              </Grid>
              <IconButton
                sx={{ position: "absolute", top: 0, right: 0 }}
                onClick={(e) => {
                  deleteItem(e, cart.name);
                }}
              >
                <Close fontSize="small" />
              </IconButton>
            </Card>
          ))}
        </Box>

        <Box
          sx={{
            width: "100%",
            flexGrow: "1",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            paddingX: "5vw",
          }}
        >
          <Grid container spacing={2}>
            <Grid
              item
              md={8}
              xs={8}
              sx={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
              }}
            >
              <div
                style={{
                  display: "flex",
                  justifyContent: "center",
                  alignItems: "center",
                }}
              >
                <IconButton onClick={ShoppingBack}>
                  <ReplyIcon />
                </IconButton>

                <Typography sx={{ fontSize: "15px", fontWeight: "bold" }}>
                  Back to shopping
                </Typography>
              </div>

              <Typography sx={{ fontWeight: "bold" }}>
                Sub Total: 1250/
              </Typography>
            </Grid>
            <Grid
              item
              md={4}
              xs={4}
              sx={{ display: "flex", justifyContent: "flex-end" }}
            >
              <Button
                variant="contained"
                sx={{
                  mt: 3,
                  textTransform: "none",
                  color: "#fff",
                }}
              >
                <Button
                  onClick={navigateLogin}
                  variant="contained"
                  size="small"
                >
                  Checkout
                </Button>
              </Button>
            </Grid>
          </Grid>
        </Box>
      </Box>
      <br />
      <br />
      <Footer />
    </>
  );
}

export default Cart;
